INSERT INTO preguntas_dificil(id, pregunta, codigo, respuesta) VALUES (1, "Cual es el valor de resultado?", "def funcion_a(x):\n    return x * 2\n\ndef funcion_b(x):\n    return x + 5\n\ndef funcion_c(x):\n    return funcion_a(x) + funcion_b(x)\n\nresultado = funcion_c(10)", "35");
INSERT INTO preguntas_dificil(id, pregunta, codigo, respuesta) VALUES (2, "Cual es el valor de resultado?", "def funcion_recursiva(n):\n    if n <= 0:\n        return 0\n    return n + funcion_recursiva(n - 1)\n\nresultado = funcion_recursiva(4)", "10");
INSERT INTO preguntas_dificil(id, pregunta, codigo, respuesta) VALUES (3, "Cual es el valor de resultado?", "def funcion(cadena):\n    letras = 'aeiouAEIOU'\n    contador = 0\n    for letra in cadena:\n        if letra in letras:\n            contador += 1\n    return contador\n\nfrase = 'Hola mundo'\nresultado = funcion(frase)", "4");
INSERT INTO preguntas_dificil(id, pregunta, codigo, respuesta) VALUES (4, "Cual es el valor de resultado?", "def funcion(lista):\n    nueva_lista = []\n    for num in lista:\n        nueva_lista.append(num * 2)\n    return nueva_lista\n\nnumeros = [1, 2, 3, 4]\nresultado = funcion(numeros)", "[2, 4, 6, 8]");
INSERT INTO preguntas_dificil(id, pregunta, codigo, respuesta) VALUES (5, "Cual es el valor de resultado?", "def suma_pares_hasta(n):\n    suma = 0\n    for num in range(n + 1):\n        if num % 2 == 0:\n            suma += num\n    return suma\n\nresultado = suma_pares_hasta(10)", "30");
INSERT INTO preguntas_dificil(id, pregunta, codigo, respuesta) VALUES (6, "Cual es el valor de resultado?", "def funcion(cadena1, cadena2):\n    return sorted(cadena1.lower()) == sorted(cadena2.lower())\n\npalabra1 = 'Listen'\npalabra2 = 'Silent'\nresultado = funcion(palabra1, palabra2)", "True");
