INSERT INTO preguntas_dificil(id, pregunta, codigo, respuesta) VALUES (8, "Cual es el valor de resultado?", "def funcion(cadena):\n    return cadena.lower() == cadena.lower()[::-1]\n\npalabra = 'reconocer'\nresultado = funcion(palabra)", "True");